import React from "react";

function BookItem({id, cover, title, price}){
    return (
        <div class="card">
            <img src={cover} alt={title} style={{width:"100%"}}/>
            <h1>{title}</h1>
            <p className="price">Kshs. {price}</p>
            <p><button>Add to Cart</button></p>
        </div>
    )

}

export default BookItem
